    // Combine LAST_UPDATE and REGISTERED_AT to form a full timestamp
    let timestamp = `${item.LAST_UPDATE} ${item.REGISTERED_AT}`;
    // Convert timestamp to a JavaScript Date object (assuming times are local)
    let date = new Date(timestamp.replace(/\./g, ':')); // Replace dots with colons in time part

    // Push an object with 'x' as the timestamp and 'y' as the numeric value of PROCESS_CURRENT_VALUE
    chartData.push({
        x: date.getTime(), // Chart node expects timestamp in milliseconds
        y: parseFloat(item.PROCESS_CURRENT_VALUE) // Ensure the value is a number
    });
});

// Prepare the message to be sent to the chart node
// Assuming you are charting data for a single series
msg.payload = [{
    "series": ["BOIL Process"],
    "data": [chartData], // Wrap chartData in an array because the chart node expects an array of datasets
    "labels": [""]
}];

